data.json_data_schema:
{
    "description": "FHIR Output Create",
    "type": "object",
    "required": [
        "serverUrl",
        "api",
        "method"
    ],
    "properties": {
        "label": {
            "title": "Label",
            "type": "string"
        },
        "serverUrl": {
            "title": "URL Servidor FHIR",
            "type": "string"
        },
        "fhir_params": {
            "title": "Servidor FHIR",
            "type": "object",
            "required": [
                "fhirVersion"
            ],
            "properties": {
                "fhirVersion": {
                    "title": "Versión FHIR",
                    "type": "string",
                    "enum": [
                        "DSTU2",
                        "DSTU2_HL7ORG",
                        "DSTU2_1",
                        "DSTU3",
                        "R4",
                        "R5"
                    ],
                    "enumNames": [
                        "DSTU2",
                        "DSTU2_HL7ORG",
                        "DSTU2_1",
                        "DSTU3",
                        "R4",
                        "R5"
                    ]
                }
            }
        },
        "method": {
            "title": "Método",
            "type": "string",
            "enum": [
                "resource"
            ],
            "enumNames": [
                "resource"
            ]
        },
        "resource_params": {
            "title": "Resource parámetros",
            "type": "object",
            "required": [
                "resource"
            ],
            "properties": {
                "resource": {
                    "title": "resource",
                    "type": "string"
                }
            }
        },
        "extra_query_params": {
            "title": "Parámetros Extra",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        }
    }
}
